mapper.insert:
insert into tb_member (idx, idx_code, user_name,
pwd, create_time, update_time,
create_by_member_idx_code, update_by_member_idx_code,
version, status, ext
)
values (#{idx,jdbcType=BIGINT}, #{idxCode,jdbcType=BIGINT}, #{userName,jdbcType=VARCHAR},
#{pwd,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP},
#{createByMemberIdxCode,jdbcType=BIGINT}, #{updateByMemberIdxCode,jdbcType=BIGINT},
#{version,jdbcType=BIGINT}, #{status,jdbcType=SMALLINT}, #{ext,jdbcType=VARCHAR}
)

mapper.select:
SELECT

from tb_out_store_stock_detail from tb_member where 1 = 1

  and name = #{name}


  and pwd = #{pwd}

SELECT  idx, idx_code, name, telphone, email from tb_member where 1 = 1

  and name = #{name}


  and pwd = #{pwd}

SELECT COUNT(idx) FROM tb_member where 1 != 0

  and level = #{level}


  and pidx = #{pidx}

SELECT   from tb_member where 1 != 0

  and name = #{name}


  and pwd = #{pwd}
